import cv2from PIL import Imagevideo_filename = '/Users/wangbiao/github/my/PythonFile/TensorFlowDemo/Deno/视频识别/myTest/openCV/res/test.mp4'videoCap = cv2.VideoCapture(video_filename)# 帧频fps = videoCap.get(cv2.CAP_PROP_FPS)# 视频总帧数total_frames = int(videoCap.get(cv2.CAP_PROP_FRAME_COUNT))# 图像尺寸image_size = (int(videoCap.get(cv2.CAP_PROP_FRAME_HEIGHT)), int(videoCap.get(cv2.CAP_PROP_FRAME_WIDTH)))print(fps)print(total_frames)print(image_size)#把每一帧转行为图片sucess, frame = videoCap.read()# for i in range(total_frames):for i in range(10):    sucess, frame = videoCap.read()    img = Image.fromarray(frame)    # img.show()    #字幕截取    im = frame[:, :, 0]    # im = im[495:570, :]  # 确定字幕的范围，注意不同的视频文件剪切的索引值不同    img = Image.fromarray(im)    # img.show()# 在进行下一步分析前，需对图像做二值化处理。由于字幕的白色的，像素值为 255，将二值化阈值设为 220。    thresh = 220    _, im = cv2.threshold(im, thresh, 255, cv2.THRESH_BINARY)    img = Image.fromarray(im)    img.show()