import cv2import sysimport pytesseractif __name__ == '__main__':    # if len(sys.argv) < 2:    #     print('Usage: python ocr_simple.py image.jpg')    #     sys.exit(1)    # Read image path from command line    imPath = '/Users/wangbiao/Downloads/chinese2.jpeg'    # imPath ="/Users/wangbiao/Downloads/WechatIMG412.png"    # imPath = "/Users/wangbiao/Downloads/执行力系统模板.jpeg"    # imPath = "/Users/wangbiao/Downloads/20200117151412.jpg"    # imPath = "/Users/wangbiao/Downloads/getVerificationCode.jpeg"    # imPath = "/Users/wangbiao/Downloads/getVerificationCode(1).jpeg"    # Uncomment the line below to provide path to tesseract manually    pytesseract.pytesseract.tesseract_cmd = '/usr/local/bin/tesseract'    # Define config parameters.    # '-l eng'  for using the English language    # '--oem 1' for using LSTM OCR Engine    config = ('-l chi_sim --oem 1 --psm 3')    # Read image from disk    im = cv2.imread(imPath, cv2.IMREAD_COLOR)    # Run tesseract OCR on image    text = pytesseract.image_to_string(im, config=config)    # Print recognized text    print(text)