import cv2import oscameraCapture = cv2.VideoCapture('/Users/wangbiao/github/my/PythonFile/TensorFlowDemo/Deno/视频识别/myTest/openCV/res/test.mp4')# cameraCapture=cv2.VideoCapture('/Users/wangbiao/Downloads/Video/云栖大会演讲-视频人脸识别-视频图像识别 - 阿里云视频AI.mp4')#  os.getcwd() +path = '/Users/wangbiao/github/my/PythonFile/TensorFlowDemo/Deno/视频识别/myTest/openCV//XML/haarcascade_frontalface_alt_tree.xml'detector = cv2.CascadeClassifier(path)cv2.namedWindow('Test camera')success, frame = cameraCapture.read()while success:    if cv2.waitKey(1) == 27:        break    success, frame = cameraCapture.read()    rects = detector.detectMultiScale(frame, scaleFactor=1.1, minNeighbors=2, minSize=(100, 100),                                      flags=cv2.CASCADE_SCALE_IMAGE)    if len(rects) > 0:        for (x, y, w, h) in rects:            cv2.rectangle(frame, (x, y), (x + w, y + h), (0, 0, 255), 1)        cv2.imshow('Test camera', frame)cv2.destroyAllWindows()cameraCapture.release()