# . POST 提交请求登录import requestsheaders = {    'User-Agent': 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/66.0.3359.181 Safari/537.36',    'authority': 'www.itjuzi.com',    'method': 'POST',    'path': '/api/authorizations',    'scheme': 'https',    'accept': 'application/json, text/plain, */*',    'accept-encoding': 'gzip, deflate, br',    'accept-language': 'zh-CN,zh;q=0.9',    'cache-control': 'no-cache',    'content-length': '66',    'content-type': 'application/json;charset=UTF-8',    'curlopt_followlocation': 'true',    'origin': 'https://www.itjuzi.com',    'pragma': 'no-cache',    'referer': 'https://www.itjuzi.com/login',    'sec-fetch-dest': 'empty',    'sec-fetch-mode': 'cors',    'sec-fetch-site': 'same-origin'    }data = {    'account':'irw27812@awsoo.com',    'password':'test2018',    'type': 'pswd'}# https://www.itjuzi.com/api/authorizationsurl ='https://www.itjuzi.com/user/login?redirect=&flag=&radar_coupon='session = requests.Session()session.post(url,headers = headers,data = data)# 登录后，我们需要获取另一个网页中的内容response = session.get('https://www.itjuzi.com/investevent',headers = headers)print(response.status_code)print(response.text)