# Selenium 模拟登录from selenium import webdriverfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.support import expected_conditions as ECfrom selenium.webdriver.support.wait import WebDriverWaitbrowser = webdriver.Chrome()browser.maximize_window()  # 最大化窗口wait = WebDriverWait(browser, 10) # 等待加载10sdef login():    browser.get('https://www.itjuzi.com/login')    input = wait.until(EC.presence_of_element_located(        (By.XPATH, '//*[@placeholder="手机号/邮箱"]')))    input.send_keys('irw27812@awsoo.com')    input = wait.until(EC.presence_of_element_located(        (By.XPATH, '//*[@placeholder="密码"]')))    input.send_keys('test2018')    submit = wait.until(EC.element_to_be_clickable(        (By.XPATH, '//*[@type="button" and @class="el-button el-button--primary"]')))    submit.click() # 点击登录按钮    get_page_index()def get_page_index():    browser.get('https://www.itjuzi.com/investevent')    try:        print(browser.page_source)  # 输出网页源码    except Exception as e:        print(str(e))login()